    log('               .-..-.');
    log('');
    log('   ' + 'Sails   ' + '           ' + '<' + '|' + '    .-..-.');
    log('   ' + message + ' |\\');
    log('                      /|.\\');
    log('                     / || \\');
    log('                   ,\'  |\'  \\');
    log('                .-\'.-==|/_--\'');
    log('                `--\'-------\' ');
    log('   __---___--___---___--___---___--___');
